import React, { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import Featured from './components/Featured';
import MovieRow from './components/MovieRow';
import Auth from './components/Auth';
import { fetchTrending, fetchPopular, fetchTopRated, fetchUpcoming } from './services/tmdb';

function App() {
  const [trending, setTrending] = useState([]);
  const [popular, setPopular] = useState([]);
  const [topRated, setTopRated] = useState([]);
  const [upcoming, setUpcoming] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  // Debug: Log component mount
  useEffect(() => {
    console.log('App component mounted');
    console.log('Environment API URL:', process.env.REACT_APP_API_URL);
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [trendingData, popularData, topRatedData, upcomingData] = await Promise.all([
          fetchTrending(),
          fetchPopular(),
          fetchTopRated(),
          fetchUpcoming()
        ]);

        setTrending(trendingData);
        setPopular(popularData);
        setTopRated(topRatedData);
        setUpcoming(upcomingData);
      } catch (err) {
        console.error('Error fetching movie data:', err);
        // Don't set error, just use empty arrays
        console.log('Continuing without movie data...');
      } finally {
        setLoading(false);
      }
    };

    if (isAuthenticated) {
      fetchData();
    }
  }, [isAuthenticated]);

  const handleAuthSuccess = (userData) => {
    setIsAuthenticated(true);
    setUser(userData);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUser(null);
  };

  // Debug info
  console.log('Environment API URL:', process.env.REACT_APP_API_URL);
  console.log('Is Authenticated:', isAuthenticated);

  // For Vercel deployment, show demo mode if backend is not available
  const isDemoMode = !process.env.REACT_APP_API_URL || 
                     process.env.REACT_APP_API_URL.includes('your-backend-url') ||
                     process.env.REACT_APP_API_URL.includes('vercel.app/api');

  console.log('Is Demo Mode:', isDemoMode);

  // Fallback: Always show Auth if not authenticated, regardless of demo mode
  if (!isAuthenticated) {
    console.log('Showing Auth component');
    return <Auth onAuthSuccess={handleAuthSuccess} />;
  }

  // Always show the app, even if movie data fails to load
  if (loading) {
    console.log('Showing loading state');
    return (
      <div className="loading">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  console.log('Showing main app');
  return (
    <div className="App">
      <Navbar user={user} onLogout={handleLogout} />
      {isDemoMode && (
        <div style={{
          backgroundColor: '#e50914',
          color: 'white',
          padding: '10px',
          textAlign: 'center',
          fontSize: '14px'
        }}>
          ðŸŽ¬ Demo Mode - Running without backend authentication
        </div>
      )}
      <Featured movies={trending} />
      <div className="movie-rows">
        <MovieRow title="Trending Now" movies={trending} />
        <MovieRow title="Popular on Netflix" movies={popular} />
        <MovieRow title="Top Rated" movies={topRated} />
        <MovieRow title="Upcoming" movies={upcoming} />
      </div>
    </div>
  );
}

export default App;
